SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ajustar_prescr_mat_assoc_gas ( nr_prescricao_p bigint, nr_sequencia_p bigint, cd_perfil_p bigint, nr_seq_material_p bigint) AS $body$
DECLARE

 
nr_ocorrencia_w			bigint;
nr_sequencia_w			integer;
cd_material_w			integer;
qt_unitaria_w			double precision;
qt_material_w			double precision;
qt_dose_especial_w		double precision;
ds_dose_diferenciada_w	varchar(50);
ie_origem_inf_w			varchar(1);
qt_dispensar_w			double precision;
qt_dose_w				double precision;
ds_erro_w				varchar(255);
cd_intervalo_w			varchar(7);
ie_se_necessario_w		varchar(1);
ie_urgencia_w			varchar(1);
ie_suspenso_w			varchar(1);
ie_via_aplicacao_w		varchar(5);
cd_unidade_medida_dose_w	varchar(30);
dt_primeiro_horario_w	timestamp;
nr_horas_validade_w		integer;
ds_horarios_w			varchar(255);
ds_horarios2_w			varchar(255);
ds_horarios3_w			varchar(255);
dt_prev_execucao_w		timestamp;
ie_regra_disp_w			varchar(1);
qt_hora_intervalo_w		smallint;
qt_min_intervalo_w		integer;
dt_prev_execucao_proc_w	timestamp;
ie_se_necessario_mat_w	varchar(1);
	
C01 CURSOR FOR 
SELECT	coalesce(b.cd_intervalo, a.cd_intervalo), 
	coalesce(b.nr_sequencia,0), 
	coalesce(b.ie_se_necessario,'N'), 
	a.dt_prev_execucao, 
	a.qt_min_intervalo, 
	c.dt_primeiro_horario, 
	c.nr_horas_validade, 
	coalesce(b.cd_material,0), 
 	coalesce(b.qt_unitaria,0), 
	coalesce(b.qt_material,0), 
	coalesce(b.qt_dose_especial,0), 
	coalesce(b.ds_dose_diferenciada,''), 
	coalesce(b.ie_origem_inf,'1'), 
	b.ie_via_aplicacao, 
	b.cd_unidade_medida_dose, 
	CASE WHEN coalesce(a.ie_suspenso,'N')='N' THEN coalesce(b.ie_suspenso,'N')  ELSE coalesce(a.ie_suspenso,'N') END , 
	substr(a.ds_horarios,1,255), 
	b.qt_dose 
from	prescr_medica c, 
		prescr_material b, 
		prescr_gasoterapia a	 
where	a.nr_prescricao = b.nr_prescricao 
and		c.nr_prescricao	= a.nr_prescricao 
and		b.nr_prescricao = c.nr_prescricao 
and		b.nr_seq_gasoterapia = a.nr_sequencia 
and		a.nr_sequencia = nr_sequencia_p 
and		a.nr_prescricao = nr_prescricao_p 
and		b.ie_agrupador = 15 
and		((b.nr_sequencia = nr_seq_material_p) or (coalesce(nr_seq_material_p,0) = 0));	
 

BEGIN 
 
open C01;
loop 
fetch C01 into 
	cd_intervalo_w, 
	nr_sequencia_w, 
	ie_se_necessario_mat_w, 
	dt_prev_execucao_w, 
	qt_min_intervalo_w, 
	dt_primeiro_horario_w, 
	nr_horas_validade_w, 
	cd_material_w, 
	qt_unitaria_w, 
	qt_material_w, 
	qt_dose_especial_w, 
	ds_dose_diferenciada_w, 
	ie_origem_inf_w, 
	ie_via_aplicacao_w, 
	cd_unidade_medida_dose_w, 
	ie_suspenso_w, 
	ds_horarios3_w, 
	qt_dose_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
 
	qt_material_w	:= 0;
	nr_ocorrencia_w	:= 0;
	SELECT * FROM Calcular_Horario_Prescricao(nr_prescricao_p, cd_intervalo_w, dt_primeiro_horario_w, to_date(to_char(dt_prev_execucao_w,'hh24:mi'),'hh24:mi'), nr_horas_validade_w, cd_material_w, qt_hora_intervalo_w, qt_min_intervalo_w, nr_ocorrencia_w, ds_horarios_w, ds_horarios2_w, 'N', null) INTO STRICT nr_ocorrencia_w, ds_horarios_w, ds_horarios2_w;
	 
	ds_horarios_w	:= ds_horarios_w || ds_horarios2_w;
 
	SELECT * FROM Obter_Quant_Dispensar(wheb_usuario_pck.get_cd_estabelecimento, cd_material_w, nr_prescricao_p, nr_sequencia_w, cd_intervalo_w, ie_via_aplicacao_w, qt_unitaria_w, coalesce( qt_dose_especial_w,0), nr_ocorrencia_w, ds_dose_diferenciada_w, ie_origem_inf_w, cd_unidade_medida_dose_w, 1, qt_material_w, qt_dispensar_w, ie_regra_disp_w, ds_erro_w, 'N', 'N') INTO STRICT qt_material_w, qt_dispensar_w, ie_regra_disp_w, ds_erro_w;
	 
	Update	prescr_material 
	set	cd_intervalo		= cd_intervalo_w, 
		ie_acm				= 'N', 
		ie_se_necessario	= 'N', 
		ie_urgencia			= 'N', 
		ie_suspenso			= ie_suspenso_w, 
		ie_utiliza_kit		= 'N', 
		ie_medicacao_paciente	= 'N', 
		hr_prim_horario		= to_char(dt_prev_execucao_w,'hh24:mi'), 
		qt_hora_intervalo	= qt_hora_intervalo_w, 
		qt_min_intervalo	= qt_min_intervalo_w, 
		ds_horarios			= coalesce(ds_horarios3_w,ds_horarios_w), 
		nr_ocorrencia		= nr_ocorrencia_w, 
		qt_total_dispensar	= qt_dispensar_w, 
		qt_material			= qt_material_w, 
		ie_regra_disp		= CASE WHEN coalesce(ie_regra_disp,'X')='D' THEN  ie_regra_disp  ELSE ie_regra_disp_w END , 
		qt_dose				= qt_dose_w 
	where	nr_prescricao	= nr_prescricao_p 
	and	nr_sequencia		= nr_sequencia_w 
	and	coalesce(ie_intervalo_dif,'N')	<> 'S';
 
end loop;
close C01;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ajustar_prescr_mat_assoc_gas ( nr_prescricao_p bigint, nr_sequencia_p bigint, cd_perfil_p bigint, nr_seq_material_p bigint) FROM PUBLIC;

