CREATE OR REPLACE PROCEDURE ajustar_protocolo_mat_medic ( cd_protocolo_p bigint, nr_sequencia_p bigint, nr_seq_material_p bigint) AS $body$
DECLARE



nr_seq_material_w	integer;
cd_intervalo_w		varchar(7);
ie_acm_w		varchar(1);
ie_se_necessario_w	varchar(1);


C01 CURSOR FOR
SELECT	coalesce(nr_seq_material,0)
from	protocolo_medic_material
where	cd_protocolo	= cd_protocolo_p
and	nr_sequencia	= nr_sequencia_p
and	nr_seq_diluicao	= nr_seq_material_p
and	(cd_material IS NOT NULL AND cd_material::text <> '');


BEGIN


begin
Select	cd_intervalo,
	coalesce(ie_se_necessario,'N'),
	coalesce(ie_acm,'N')
into STRICT	cd_intervalo_w,
	ie_se_necessario_w,
	ie_acm_w
from	protocolo_medic_material
where	cd_protocolo	= cd_protocolo_p
and	nr_sequencia	= nr_sequencia_p
and	nr_seq_material	= nr_seq_material_p;


exception
	when others then
	null;

end;


open C01;
loop
	fetch C01 into
		nr_seq_material_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

	Update	protocolo_medic_material
	set	ie_acm		= ie_acm_w,
		ie_se_necessario = ie_se_necessario_w
	where	cd_protocolo	= cd_protocolo_p
	and	nr_sequencia	= nr_sequencia_p
	and	nr_seq_diluicao	= nr_seq_material_p
	and	ie_agrupador in (3,7,9);

end loop;
close C01;


commit;

end;
$body$
