CREATE OR REPLACE PROCEDURE ajustar_qtde_proc_paciente ( nr_interno_conta_p bigint, cd_medico_executor_p text, dt_acerto_conta_p timestamp, dt_procedimento_p timestamp, cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_medico_req_p text, nr_seq_exame_p bigint, ie_tecnica_utilizada_p text, qt_procedimento_p bigint, cd_setor_atendimento_p bigint, qt_devolvida_p bigint, cd_acao_p bigint, nm_usuario_p text, nr_atendimento_p bigint, nr_seq_proc_interno_p bigint, ie_funcao_medico_p text, ie_via_acesso_p text, vl_procedimento_p bigint) AS $body$
DECLARE


dt_entrada_unidade_w		timestamp;
nr_seq_cor_exec_w		bigint	:= 369;
nr_seq_interno_w		bigint;


BEGIN

select  max(a.dt_entrada_unidade),
	max(a.nr_seq_interno)
into STRICT	dt_entrada_unidade_w,
	nr_seq_interno_w
from	tipo_acomodacao b,
	setor_atendimento c,
	atend_paciente_unidade a
where	a.cd_tipo_acomodacao	= b.cd_tipo_acomodacao
and	a.cd_setor_atendimento  = c.cd_setor_atendimento
and	nr_atendimento		= nr_atendimento_p
and	a.cd_setor_atendimento  = cd_setor_atendimento_p;


insert into procedimento_paciente(
			nr_sequencia,
			nr_atendimento,
			dt_entrada_unidade,
			dt_conta,
			cd_procedimento,
			dt_procedimento,
			ie_origem_proced,
			nr_seq_exame,
			nr_seq_proc_interno,
			qt_procedimento,
			cd_setor_atendimento,
			nr_seq_atepacu,
			nr_seq_cor_exec,
			ie_funcao_medico,
			nr_interno_conta,
			cd_medico_executor,
			dt_acerto_conta,
			cd_medico_req,
			ie_tecnica_utilizada,
			qt_devolvida,
			cd_acao,
			ie_via_acesso,
			vl_procedimento,
			dt_atualizacao,
			nm_usuario)
		values (
			nextval('procedimento_paciente_seq'),
			nr_atendimento_p,
			dt_entrada_unidade_w,
			dt_procedimento_p,
			cd_procedimento_p,
			dt_procedimento_p,
			ie_origem_proced_p,
			CASE WHEN nr_seq_exame_p=0 THEN  null  ELSE nr_seq_exame_p END ,
			CASE WHEN nr_seq_proc_interno_p=0 THEN  null  ELSE nr_seq_proc_interno_p END ,
			qt_procedimento_p,
			cd_setor_atendimento_p,
			nr_seq_interno_w,
			nr_seq_cor_exec_w,
			CASE WHEN ie_funcao_medico_p='' THEN  null  ELSE ie_funcao_medico_p END ,
			nr_interno_conta_p,
			cd_medico_executor_p,
			dt_acerto_conta_p,
			CASE WHEN cd_medico_req_p='' THEN  null  ELSE cd_medico_req_p END ,
			ie_tecnica_utilizada_p,
			qt_devolvida_p,
			cd_acao_p,
			CASE WHEN ie_via_acesso_p='' THEN  null  ELSE ie_via_acesso_p END ,
			coalesce(vl_procedimento_p, 0),
			clock_timestamp(),
			nm_usuario_p);

commit;

end;
$body$
