CREATE OR REPLACE PROCEDURE ajustar_req_proj_migr_audit ( nr_seq_projeto_p bigint, dt_atualizacao_p timestamp, nm_usuario_p text) AS $body$
DECLARE


nr_seq_requisito_w	bigint;
nr_seq_requisito_item_w	bigint;
ds_funcao_w		varchar(80);
ds_requisito_w		varchar(4000);
ie_status_w		varchar(1);


BEGIN
if (nr_seq_projeto_p IS NOT NULL AND nr_seq_projeto_p::text <> '') and (dt_atualizacao_p IS NOT NULL AND dt_atualizacao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	nextval('des_requisito_seq')
	into STRICT	nr_seq_requisito_w
	;

	insert into des_requisito(
		nr_sequencia,
		nr_seq_projeto,
		ds_titulo,
		cd_analista,
		dt_requisito,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ie_cliente_wheb,
		nr_seq_apresentacao,
		dt_aprovacao,
		nr_seq_ordem_serv,
		ie_relatorio)
	values (
		nr_seq_requisito_w,
		nr_seq_projeto_p,
		'Requisitos do Projeto',
		'4464',
		dt_atualizacao_p,
		dt_atualizacao_p,
		nm_usuario_p,
		dt_atualizacao_p,
		nm_usuario_p,
		'W',
		10,
		trunc(dt_atualizacao_p),
		null,
		'S');

	select	nextval('des_requisito_item_seq')
	into STRICT	nr_seq_requisito_item_w
	;

	select	f.ds_funcao,
		CASE WHEN p.nr_seq_estagio=13 THEN 'A' WHEN p.nr_seq_estagio=17 THEN 'A'  ELSE 'N' END
	into STRICT	ds_funcao_w,
		ie_status_w
	from	funcao f,
		proj_projeto p
	where	f.cd_funcao = p.cd_funcao
	and	p.nr_sequencia = nr_seq_projeto_p;

	ds_requisito_w	:=	'Realizar a migração/conversão da função ' || ds_funcao_w || ' para a plataforma Java utilizando a tecnologia Swing e os ' ||
				'componentes desenvolvidos internamente neste sentido, e, mantendo as funcionalidades existentes na sua versão Delphi.';

	insert into des_requisito_item(
		nr_sequencia,
		nr_seq_requisito,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_apresentacao,
		ds_item,
		ds_titulo,
		ie_status,
		ie_tipo_requisito,
		pr_realizacao)
	values (
		nr_seq_requisito_item_w,
		nr_seq_requisito_w,
		dt_atualizacao_p,
		nm_usuario_p,
		dt_atualizacao_p,
		nm_usuario_p,
		10,
		ds_requisito_w,
		'Migração da função ' || ds_funcao_w,
		ie_status_w,
		'RF',
		null);
	commit;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
