CREATE OR REPLACE PROCEDURE ajustar_status_anexo_opme ( nr_seq_agenda_p bigint, ie_status_integracao_p bigint, nm_usuario_p text, ie_commit_p text default 'S') AS $body$
DECLARE



qt_existe_anexos_w	bigint;
ie_opme_int_status_w	varchar(80);
nr_seq_status_w		bigint;
qt_existe_regra_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_anexos_w
from	anexo_agenda
where 	nr_seq_agenda = nr_seq_agenda_p;

select 	count(*)
into STRICT	qt_existe_regra_w
from 	opme_ajuste_status_anexo
where 	ie_opme_int_status = ie_status_integracao_p;

if (coalesce(qt_existe_anexos_w,0) > 0 ) and (coalesce(qt_existe_regra_w,0) > 0 )then
	begin
	select	ie_opme_int_status,
		nr_seq_status
	into STRICT	ie_opme_int_status_w,
		nr_seq_status_w
	from 	opme_ajuste_status_anexo
	where 	ie_opme_int_status = ie_status_integracao_p;

	update	anexo_agenda
	set	nr_seq_status = nr_seq_status_w,
		nm_usuario = nm_usuario_p
	where	nr_seq_agenda = nr_seq_agenda_p;

	end;
end if;

if (coalesce(ie_commit_p,'S') = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
