SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ajustar_suplementos_dieta ( nr_prescricao_p bigint, nr_seq_dieta_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_material_w				integer;
ie_via_aplicacao_w			varchar(5);
cd_unidade_medida_dose_w	varchar(30);
qt_dose_w					double precision;
nr_sequencia_w				bigint;
qt_conversao_dose_w			double precision;
qt_unitaria_w				double precision;
ie_origem_inf_w				varchar(1);
cd_estabelecimento_w		smallint;
nr_ocorrencia_w				double precision;
ds_horarios_w				varchar(2000);
cd_intervalo_w				varchar(10);
qt_material_w				double precision;
qt_total_dispensar_w		double precision;
ie_regra_disp_w				varchar(1);
qt_dose_especial_w			double precision;
qt_dias_util_w				double precision;
ds_erro_w					varchar(4000);
nr_horas_validade_w			bigint;
ie_se_necessario_w			varchar(1);
ie_acm_w				varchar(1);
				
c01 CURSOR FOR 
SELECT	nr_sequencia, 
		cd_material, 
		ie_via_aplicacao, 
		cd_unidade_medida_dose, 
		qt_dose, 
		coalesce(ie_se_necessario,'N'), 
		coalesce(ie_acm,'N') 
from	prescr_material 
where	nr_prescricao 		= nr_prescricao_p 
and		nr_sequencia_dieta	= nr_seq_dieta_p;


BEGIN 
 
select	substr(coalesce(obter_tipo_pessoa(max(cd_pessoa_fisica)),0),1,1), 
		max(cd_estabelecimento) 
into STRICT	ie_origem_inf_w, 
		cd_estabelecimento_w 
from	usuario 
where	nm_usuario	= nm_usuario_p;
 
select	max(cd_intervalo), 
		max(ds_horarios) 
into STRICT	cd_intervalo_w, 
		ds_horarios_w 
from	prescr_dieta 
where	nr_prescricao	= nr_prescricao_p 
and		nr_sequencia	= nr_seq_dieta_p;
 
select	max(nr_horas_validade) 
into STRICT	nr_horas_validade_w 
from	prescr_medica 
where	nr_prescricao = nr_prescricao_p;
 
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w, 
	cd_material_w, 
	ie_via_aplicacao_w, 
	cd_unidade_medida_dose_w, 
	qt_dose_w, 
	ie_se_necessario_w, 
	ie_acm_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	coalesce(max(qt_conversao),0) 
	into STRICT	qt_conversao_dose_w 
	from	unidade_medida_dose_v 
	where	cd_material		= cd_material_w 
	and		cd_unidade_medida	= cd_unidade_medida_dose_w;
	 
	if (qt_conversao_dose_w	= 0) then 
		qt_unitaria_w	:= 0;
	else 
		qt_unitaria_w	:= round((qt_dose_w / qt_conversao_dose_w)::numeric,6);
	end if;	
	 
	select	max(obter_ocorrencias_horarios_rep(ds_horarios_w)) 
	into STRICT	nr_ocorrencia_w 
	;
	 
	if (nr_ocorrencia_w = 0) then 
		select	coalesce(obter_ocorrencia_intervalo(cd_intervalo_w,nr_horas_validade_w,'O'),1) 
		into STRICT	nr_ocorrencia_w 
		;
	end if;
	 
	qt_material_w := qt_dose_w;
			 
	SELECT * FROM obter_quant_dispensar( 
		cd_estabelecimento_w, cd_material_w, nr_prescricao_p, nr_sequencia_w, cd_intervalo_w, ie_via_aplicacao_w, qt_unitaria_w, 0, nr_ocorrencia_w, null, ie_origem_inf_w, cd_unidade_medida_dose_w, 0, qt_material_w, qt_total_dispensar_w, ie_regra_disp_w, ds_erro_w, ie_se_necessario_w, ie_acm_w) INTO STRICT qt_material_w, qt_total_dispensar_w, ie_regra_disp_w, ds_erro_w;
		 
	update	prescr_material 
	set		qt_material			= qt_material_w, 
			cd_intervalo		= cd_intervalo_w, 
			ds_horarios			= ds_horarios_w, 
			qt_total_dispensar	= qt_total_dispensar_w, 
			ie_regra_disp		= CASE WHEN coalesce(ie_regra_disp,'X')='D' THEN  ie_regra_disp  ELSE ie_regra_disp_w END , 
			nr_ocorrencia 		= nr_ocorrencia_w 
	where	nr_prescricao		= nr_prescricao_p 
	and		nr_sequencia		= nr_sequencia_w;
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
