SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ajustar_tempo_prev_os ( nr_seq_gerencia_p bigint, dt_inicial_p timestamp, dt_final_p timestamp) AS $body$
DECLARE


nm_usuario_exec_w		varchar(255);
dt_atividade_w			timestamp;
qt_minuto_w			bigint;
nr_seq_ordem_serv_w		bigint;
nr_seq_funcao_w			bigint;

C01 CURSOR FOR
	SELECT	a.nm_usuario_exec,
		trunc(a.dt_atividade,'dd'),
		sum(a.qt_minuto),
		a.nr_seq_ordem_serv,
		a.nr_seq_funcao
	from	man_ordem_serv_ativ a
	where	exists (SELECT	1
			from	usuario_grupo_des c,
				grupo_desenvolvimento b
			where	b.nr_sequencia		= c.nr_seq_grupo
			and	b.nr_seq_gerencia	= nr_seq_gerencia_p
			and	c.nm_usuario_grupo	= a.nm_usuario_exec)
	and	not exists (	select	1
				from	man_ordem_ativ_prev x
				where	x.nr_seq_ordem_serv	= a.nr_seq_ordem_serv
				and	trunc(a.dt_atividade,'dd')	= trunc(x.dt_prevista,'dd')
				and	x.nm_usuario_prev	= a.nm_usuario_exec)
	and	a.dt_atividade between trunc(dt_inicial_p) and fim_dia(dt_final_p)
	group by a.nm_usuario_exec,
		trunc(a.dt_atividade,'dd'),
		a.nr_seq_ordem_serv,
		a.nr_seq_funcao;


BEGIN

open C01;
loop
fetch C01 into
	nm_usuario_exec_w,
	dt_atividade_w,
	qt_minuto_w,
	nr_seq_ordem_serv_w,
	nr_seq_funcao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	insert into man_ordem_ativ_prev(	nr_sequencia, nr_seq_ordem_serv, dt_atualizacao, nm_usuario, ds_atividade, dt_prevista,
						dt_real, nr_seq_estagio, ds_observacao, qt_min_prev, nm_usuario_real, dt_atualizacao_nrec,
						nm_usuario_nrec, nr_seq_funcao, nm_usuario_prev, qt_hora_prev, vl_cobranca_venda, vl_cobranca,
						nr_seq_grupo_trabalho, nr_seq_apres, nr_seq_ativ_exec, qt_atividade, qt_min_padrao_ativ)
					values (	nextval('man_ordem_ativ_prev_seq'), nr_seq_ordem_serv_w, clock_timestamp(), 'Tasy', null, dt_atividade_w,
						null, null, null, qt_minuto_w, null, clock_timestamp(),
						'Tasy', nr_seq_funcao_w, nm_usuario_exec_w,round((qt_minuto_w/60)::numeric,2), null, null,
						null, null, null, 1, null);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
