CREATE OR REPLACE PROCEDURE ajustar_valores_pacote ( nr_atendimento_p bigint, cd_convenio_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_proc_w					bigint;
tx_procedimento_w				double precision;
pr_afaturar_w					double precision;
vl_procedimento_w				double precision;
vl_custo_operacional_w				double precision;
vl_medico_w					double precision;

c010 CURSOR FOR
SELECT 	a.nr_seq_procedimento,
		a.pr_afaturar,
		b.vl_procedimento,
		b.tx_procedimento,
		coalesce(b.vl_custo_operacional,0),
		coalesce(b.vl_medico, 0)
from Procedimento_paciente b, atendimento_pacote a
where a.nr_atendimento		= nr_atendimento_p
  and a.cd_convenio		= cd_convenio_p
  and a.nr_seq_procedimento 	= b.nr_sequencia
  and a.pr_afaturar <> 100
  and a.pr_afaturar <> b.tx_procedimento;


BEGIN

OPEN C010;
LOOP
FETCH C010 into
	nr_seq_proc_w,
	pr_afaturar_w,
	vl_procedimento_w,
	tx_procedimento_w,
	vl_custo_operacional_w,
	vl_medico_w;
EXIT WHEN NOT FOUND; /* apply on C010 */
	begin
	vl_procedimento_w		:= vl_procedimento_w / tx_procedimento_w;
	vl_procedimento_w		:= vl_procedimento_w * pr_afaturar_w;

	if (vl_custo_operacional_w <> 0) then
		vl_custo_operacional_w		:= vl_custo_operacional_w / tx_procedimento_w;
		vl_custo_operacional_w		:= vl_custo_operacional_w * pr_afaturar_w;
	end if;

	if (vl_medico_w <> 0) then
		vl_medico_w		:= vl_medico_w / tx_procedimento_w;
		vl_medico_w		:= vl_medico_w * pr_afaturar_w;
	end if;

	update procedimento_paciente
	set 	tx_procedimento 	= pr_afaturar_w,
		vl_procedimento 	= vl_procedimento_w,
		vl_custo_operacional	= CASE WHEN vl_custo_operacional=0 THEN  0  ELSE vl_procedimento_w END ,
		vl_medico		= CASE WHEN vl_medico=0 THEN  0  ELSE vl_medico_w END
	where 	nr_sequencia = nr_seq_proc_w;
	end;
END LOOP;
close c010;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
