SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ajustar_valor_auditoria_mat ( nr_sequencia_p bigint, vl_material_p bigint, nr_seq_motivo_p bigint, ie_tipo_ajuste_p bigint, vl_unitario_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_material_w			double precision;
nr_seq_matpaci_w		bigint;
qt_material_w			double precision;
ie_Gerar_Pendencia_w		varchar(1);
vl_unitario_w			double precision;

ie_altera_item_repasse_w	varchar(1) := 'S';
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.Get_cd_estabelecimento;

BEGIN

ie_Gerar_Pendencia_w 	 := obter_valor_param_usuario(1116, 136, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w);
ie_altera_item_repasse_w := obter_valor_param_usuario(1116, 189, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w);

select	nr_seq_matpaci
into STRICT	nr_seq_matpaci_w
from	auditoria_matpaci
where	nr_sequencia	= nr_sequencia_p;

select	qt_material,
	vl_material,
	vl_unitario
into STRICT	qt_material_w,
	vl_material_w,
	vl_unitario_w
from	material_atend_paciente
where	nr_sequencia		= nr_seq_matpaci_w;


if (ie_Gerar_Pendencia_w = 'V') then

	update	auditoria_matpaci a
	set	a.vl_material_ajuste	= CASE WHEN ie_tipo_ajuste_p=1 THEN (coalesce(vl_unitario_p,0) * qt_material_w)  ELSE vl_material_p END ,
		a.ie_tipo_auditoria	= 'V',
		a.nr_seq_motivo		= CASE WHEN nr_seq_motivo_p=-1 THEN  a.nr_seq_motivo  ELSE nr_seq_motivo_p END ,
		a.nm_usuario = nm_usuario_p,
		a.dt_atualizacao = clock_timestamp()
	where	a.nr_sequencia		= nr_sequencia_p
	and	not exists (	SELECT	1
				from	material_repasse x
				where	x.nr_seq_material = a.nr_seq_matpaci
				and	coalesce(ie_altera_item_repasse_w,'S') = 'N');

else

	update	auditoria_matpaci a
	set	a.vl_material		= vl_material_w,
		a.vl_material_ajuste	= CASE WHEN ie_tipo_ajuste_p=1 THEN (coalesce(vl_unitario_p,0) * qt_material_w)  ELSE vl_material_p END ,
		a.ie_tipo_auditoria	= 'V',
		a.nr_seq_motivo		= CASE WHEN nr_seq_motivo_p=-1 THEN  a.nr_seq_motivo  ELSE nr_seq_motivo_p END ,
		a.nm_usuario = nm_usuario_p,
		a.dt_atualizacao = clock_timestamp()
	where	a.nr_sequencia		= nr_sequencia_p
	and	not exists (	SELECT	1
				from	material_repasse x
				where	x.nr_seq_material = a.nr_seq_matpaci
				and	coalesce(ie_altera_item_repasse_w,'S') = 'N');

end if;


if (ie_tipo_ajuste_p = 1) then

	update	material_Atend_Paciente a
	set	a.vl_material	= (coalesce(vl_unitario_p,0) * qt_material_w),
		a.vl_unitario   = coalesce(vl_unitario_p,0),
		a.ie_valor_informado	= 'S',
		a.nm_usuario = nm_usuario_p,
		a.dt_atualizacao = clock_timestamp()
	where	a.nr_sequencia	= nr_seq_matpaci_w
	and	not exists (	SELECT	1
				from	material_repasse x
				where	x.nr_seq_material = a.nr_sequencia
				and	coalesce(ie_altera_item_repasse_w,'S') = 'N');

else

	update	material_Atend_Paciente a
	set	a.vl_material	= vl_material_p,
		a.vl_unitario = dividir(vl_material_p,qt_material_w),
		a.ie_valor_informado	= 'S',
		a.nm_usuario = nm_usuario_p,
		a.dt_atualizacao = clock_timestamp()
	where	a.nr_sequencia	= nr_seq_matpaci_w
	and	not exists (	SELECT	1
				from	material_repasse x
				where	x.nr_seq_material = a.nr_sequencia
				and	coalesce(ie_altera_item_repasse_w,'S') = 'N');

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ajustar_valor_auditoria_mat ( nr_sequencia_p bigint, vl_material_p bigint, nr_seq_motivo_p bigint, ie_tipo_ajuste_p bigint, vl_unitario_p bigint, nm_usuario_p text) FROM PUBLIC;

