CREATE OR REPLACE PROCEDURE ajustar_valor_auditoria_partic ( nr_sequencia_p bigint, nr_seq_partic_p bigint, vl_conta_p bigint, vl_participante_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_conta_w			double precision;
vl_participante_w		double precision;


BEGIN

select	coalesce(vl_conta,0),
	coalesce(vl_participante,0)
into STRICT	vl_conta_w,
	vl_participante_w
from	procedimento_participante
where	nr_sequencia	= nr_sequencia_p
and 	nr_seq_partic	= nr_seq_partic_p;

if	((vl_conta_w <> vl_conta_p) or (vl_participante_w <> vl_participante_p)) then

	update	procedimento_participante
	set	vl_conta 		= vl_conta_p,
		vl_participante 	= vl_participante_p,
		ie_valor_informado	= 'S',
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_sequencia_p
	and 	nr_seq_partic		= nr_seq_partic_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
