SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ajustar_valor_auditoria_proc ( nr_sequencia_p bigint, vl_procedimento_p bigint, vl_materiais_p bigint, vl_Custo_Operacional_p bigint, vl_medico_p bigint, nr_seq_motivo_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_procedimento_w		double precision;
vl_materiais_w			double precision;
vl_Custo_Operacional_w		double precision;
vl_medico_w			double precision;
nr_seq_procpaci_w		bigint;
ie_Gerar_Pendencia_w		varchar(1);
ie_altera_item_repasse_w	varchar(1) := 'S';
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.Get_cd_estabelecimento;

BEGIN

ie_Gerar_Pendencia_w 	 := obter_valor_param_usuario(1116, 136, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w);
ie_altera_item_repasse_w := obter_valor_param_usuario(1116, 189, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w);

select	nr_seq_propaci
into STRICT	nr_seq_procpaci_w
from	auditoria_propaci
where	nr_sequencia	= nr_sequencia_p;

select	vl_procedimento,
	vl_materiais,
	vl_custo_operacional,
	vl_medico
into STRICT	vl_procedimento_w,
	vl_materiais_w,
	vl_custo_operacional_w,
	vl_medico_w
from	procedimento_paciente
where	nr_sequencia	= nr_seq_procpaci_w;

if (ie_Gerar_Pendencia_w = 'V') then

	update	auditoria_propaci a
	set	a.vl_procedimento_ajuste		= vl_procedimento_p,
		a.vl_materiais_ajuste		= vl_materiais_p,
		a.vl_medico_ajuste		= vl_medico_p,
		a.vl_Custo_Operacional_ajuste	= vl_Custo_Operacional_p,
		a.ie_tipo_auditoria		= 'V',
		a.nr_seq_motivo		= CASE WHEN nr_seq_motivo_p=-1 THEN  a.nr_seq_motivo  ELSE nr_seq_motivo_p END ,
		a.nm_usuario = nm_usuario_p,
		a.dt_atualizacao = clock_timestamp()
	where	a.nr_sequencia	= nr_sequencia_p
	and	not exists (	SELECT	1
				from	procedimento_repasse x
				where	x.nr_seq_procedimento = a.nr_seq_propaci
				and	coalesce(ie_altera_item_repasse_w,'S') = 'N');

else

	update	auditoria_propaci a
	set	a.vl_procedimento			= vl_procedimento_w,
		a.vl_materiais			= vl_materiais_w,
		a.vl_custo_operacional		= vl_custo_operacional_w,
		a.vl_medico			= vl_medico_w,
		a.vl_procedimento_ajuste		= vl_procedimento_p,
		a.vl_materiais_ajuste		= vl_materiais_p,
		a.vl_medico_ajuste		= vl_medico_p,
		a.vl_Custo_Operacional_ajuste	= vl_Custo_Operacional_p,
		a.ie_tipo_auditoria		= 'V',
		a.nr_seq_motivo		= CASE WHEN nr_seq_motivo_p=-1 THEN  a.nr_seq_motivo  ELSE nr_seq_motivo_p END ,
		a.nm_usuario = nm_usuario_p,
		a.dt_atualizacao = clock_timestamp()
	where	a.nr_sequencia	= nr_sequencia_p
	and	not exists (	SELECT	1
				from	procedimento_repasse x
				where	x.nr_seq_procedimento = a.nr_seq_propaci
				and	coalesce(ie_altera_item_repasse_w,'S') = 'N');

end if;

update	procedimento_paciente a
set	a.vl_procedimento			= vl_procedimento_p,
	a.vl_materiais			= vl_materiais_p,
	a.vl_custo_operacional		= vl_custo_operacional_p,
	a.vl_medico			= vl_medico_p,
	a.ie_valor_informado		= 'S'	,
	a.nm_usuario = nm_usuario_p,
	a.dt_atualizacao = clock_timestamp()
where	a.nr_sequencia			= nr_seq_procpaci_w
and	not exists (	SELECT	1
			from	procedimento_repasse x
			where	x.nr_seq_procedimento = a.nr_sequencia
			and	coalesce(ie_altera_item_repasse_w,'S') = 'N');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ajustar_valor_auditoria_proc ( nr_sequencia_p bigint, vl_procedimento_p bigint, vl_materiais_p bigint, vl_Custo_Operacional_p bigint, vl_medico_p bigint, nr_seq_motivo_p bigint, nm_usuario_p text) FROM PUBLIC;

