SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ajustar_valor_audit_perc ( nr_sequencia_p bigint, vl_indice_p bigint, nr_seq_motivo_p bigint, ie_tipo_item_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_material_w			double precision;
nr_seq_matpaci_w		bigint;
qt_material_w			double precision;

vl_procedimento_w		double precision;
vl_materiais_w			double precision;
vl_custo_operacional_w		double precision;
vl_medico_w			double precision;
nr_seq_procpaci_w		bigint;

BEGIN

if (ie_tipo_item_p = 1) then
	select	nr_seq_propaci
	into STRICT	nr_seq_procpaci_w
	from	auditoria_propaci
	where	nr_sequencia	= nr_sequencia_p;

	select	vl_procedimento,
		vl_materiais,
		vl_custo_operacional,
		vl_medico
	into STRICT	vl_procedimento_w,
		vl_materiais_w,
		vl_custo_operacional_w,
		vl_medico_w
	from	procedimento_paciente
	where	nr_sequencia	= nr_seq_procpaci_w;

	update	auditoria_propaci
	set	vl_procedimento			= CASE WHEN vl_procedimento = NULL THEN  vl_procedimento_w  ELSE vl_procedimento END ,
		vl_materiais			= CASE WHEN vl_materiais = NULL THEN  vl_materiais_w  ELSE vl_materiais END ,
		vl_custo_operacional		= CASE WHEN vl_custo_operacional = NULL THEN  vl_custo_operacional_w  ELSE vl_custo_operacional END ,
		vl_medico			= CASE WHEN vl_medico = NULL THEN  vl_medico_w  ELSE vl_medico END ,
		vl_procedimento_ajuste		= (vl_procedimento_w * vl_indice_p),
		vl_materiais_ajuste		= (vl_materiais_w * vl_indice_p),
		vl_medico_ajuste		= (vl_medico_w * vl_indice_p),
		vl_custo_operacional_ajuste	= (vl_custo_operacional_w * vl_indice_p),
		ie_tipo_auditoria		= 'V',
		nr_seq_motivo			= CASE WHEN nr_seq_motivo_p=0 THEN  nr_seq_motivo  ELSE nr_seq_motivo_p END
	where	nr_sequencia	= nr_sequencia_p;

	update	procedimento_paciente
	set	vl_procedimento		= (vl_procedimento * vl_indice_p),
		vl_materiais		= (vl_materiais * vl_indice_p),
		vl_custo_operacional	= (vl_custo_operacional * vl_indice_p),
		vl_medico		= (vl_medico * vl_indice_p),
		ie_valor_informado	= 'S'
	where	nr_sequencia		= nr_seq_procpaci_w;

else
	select	nr_seq_matpaci
	into STRICT	nr_seq_matpaci_w
	from	auditoria_matpaci
	where	nr_sequencia	= nr_sequencia_p;

	select	qt_material,
		vl_material
	into STRICT	qt_material_w,
		vl_material_w
	from	material_atend_paciente
	where	nr_sequencia	= nr_seq_matpaci_w;


	update	auditoria_matpaci
	set	vl_material		= CASE WHEN vl_material = NULL THEN  vl_material_w  ELSE vl_material END ,
		vl_material_ajuste	= (vl_material_w * vl_indice_p),
		ie_tipo_auditoria	= 'V',
		nr_seq_motivo		= CASE WHEN nr_seq_motivo_p=0 THEN  nr_seq_motivo  ELSE nr_seq_motivo_p END
	where	nr_sequencia		= nr_sequencia_p;

	update	material_atend_paciente
	set	vl_material = (vl_material * vl_indice_p),
		vl_unitario = dividir(vl_material * vl_indice_p, qt_material_w),
		ie_valor_informado	= 'S'
	where	nr_sequencia	= nr_seq_matpaci_w;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
