SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ajustar_venc_titulo_imposto ( nr_titulo_p bigint, nm_usuario_p text) AS $body$
DECLARE



-- atecao !!! nao dar commit nesta procedure
cd_estabelecimento_w	smallint;
dt_imposto_w		timestamp;
nr_seq_imposto_w		bigint;
dt_emissao_w		timestamp;
dt_vencimento_w		timestamp;
dt_liquidacao_w		timestamp;
nr_titulo_w		bigint;
cd_cond_pagto_w		bigint;
qt_venc_w		integer;
ds_venc_w		varchar(2000);
nr_seq_alt_w		bigint;
ie_dt_contab_tit_trib_w	varchar(2);

c01 CURSOR FOR
SELECT	b.cd_estabelecimento,
	b.nr_titulo,
	a.cd_cond_pagto,
	a.dt_imposto,
	a.nr_sequencia
from	tributo c,
	titulo_pagar b,
	titulo_pagar_imposto a
where	a.nr_titulo			= nr_titulo_p
and	a.nr_sequencia		= b.nr_seq_tributo
and	(dt_liquidacao_w IS NOT NULL AND dt_liquidacao_w::text <> '')
and	coalesce(a.ie_vencimento,'V')	= 'V'
and	c.cd_tributo		= a.cd_tributo
and	coalesce(c.ie_atualizar_venc_trib, 'S') = 'S';

c02 CURSOR FOR
SELECT	b.nr_titulo
from	tributo c,
	titulo_pagar b,
	titulo_pagar_imposto a
where	a.nr_titulo			= nr_titulo_p
and	a.nr_sequencia		= b.nr_seq_tributo
and	(dt_liquidacao_w IS NOT NULL AND dt_liquidacao_w::text <> '')
and	coalesce(c.ie_dt_contab_tit_trib,'X')	in ('V','L')
and	c.cd_tributo		= a.cd_tributo
and	coalesce(b.nr_lote_contabil,0) 	= 0
and	c.ie_atualizar_trib_liq 	= 'S'
and	coalesce(b.dt_liquidacao::text, '') = '';


BEGIN

select	dt_liquidacao
into STRICT	dt_liquidacao_w
from	titulo_pagar
where 	nr_titulo	= nr_titulo_p;


open c01;
loop
fetch c01 into
	cd_estabelecimento_w,
	nr_titulo_w,
	cd_cond_pagto_w,
	dt_imposto_w,
	nr_seq_imposto_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	begin

	dt_vencimento_w	:= null;
	
	if (cd_cond_pagto_w IS NOT NULL AND cd_cond_pagto_w::text <> '') then
		SELECT * FROM calcular_vencimento(cd_estabelecimento_w, cd_cond_pagto_w, dt_liquidacao_w, qt_venc_w, ds_venc_w) INTO STRICT qt_venc_w, ds_venc_w;


		if (qt_venc_w = 1) then	
			dt_vencimento_w	:= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(to_date(substr(ds_venc_w,1,10),'dd/mm/yyyy'));
		end if;
	end if;

	if (dt_vencimento_w IS NOT NULL AND dt_vencimento_w::text <> '') then
		begin

		update	titulo_pagar_imposto
		set	dt_imposto	= dt_vencimento_w
		where	nr_sequencia	= nr_seq_imposto_w;

		select	coalesce(max(nr_sequencia),0) + 1
		into STRICT	nr_seq_alt_w
		from	titulo_pagar_alt_venc
		where	nr_titulo = nr_titulo_w;
	
		insert into titulo_pagar_alt_venc(		/* rafael. os48885. 29/01/2007. */
			nr_titulo,
			nr_sequencia,
			dt_anterior,
			dt_vencimento,
			dt_atualizacao,
			nm_usuario,
			dt_alteracao)
		values (	nr_titulo_w,
			nr_seq_alt_w, 
			dt_vencimento_w,
			dt_vencimento_w,
			clock_timestamp(),
			nm_usuario_p,
			pkg_date_utils.get_time(clock_timestamp(),0,0,0));

		update	titulo_pagar
		set	dt_vencimento_atual	= dt_vencimento_w
		where	nr_titulo			= nr_titulo_w;

		end;
	end if;
	end;
end loop;
close c01;

open c02;
loop
fetch c02 into	
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on c02 */
	begin
	update	titulo_pagar
	set	dt_contabil	= dt_liquidacao_w,
		dt_emissao 	= dt_liquidacao_w
	where	nr_titulo		= nr_titulo_w;
	end;
end loop;
close c02;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ajustar_venc_titulo_imposto ( nr_titulo_p bigint, nm_usuario_p text) FROM PUBLIC;

