CREATE OR REPLACE PROCEDURE ajustar_volume_fase_sol_adep (ie_tipo_solucao_p bigint, nr_prescricao_p bigint, nr_seq_solucao_p bigint, qt_volume_p bigint) AS $body$
BEGIN
if (ie_tipo_solucao_p IS NOT NULL AND ie_tipo_solucao_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') and (qt_volume_p IS NOT NULL AND qt_volume_p::text <> '') then
	if (ie_tipo_solucao_p = 1) then -- Solucao
		update	prescr_solucao
		set	qt_volume_adep = qt_volume_p
		where	nr_prescricao = nr_prescricao_p
		and	nr_seq_solucao = nr_seq_solucao_p;

	elsif (ie_tipo_solucao_p = 2) then -- Suporte nutricional enteral
		update prescr_material
		set	   qt_volume_adep = qt_volume_p
		where  nr_prescricao = nr_prescricao_p
		and    nr_sequencia  = nr_seq_solucao_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
