CREATE OR REPLACE PROCEDURE ajusta_nr_ciclo ( nr_ciclo_informado_p bigint, nr_seq_equipamento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_retorno_p INOUT bigint) AS $body$
DECLARE



qt_caracter_w		integer := 0;
vl_soma_w		bigint;
qt_nr_ciclo_w		integer := 0;


BEGIN
ds_retorno_p := nr_ciclo_informado_p;

select	length(nr_ciclo_informado_p)
into STRICT	qt_caracter_w
;

if (qt_caracter_w = 2) then
	qt_caracter_w := 1;
elsif (qt_caracter_w = 3) then
	qt_caracter_w := 2;
elsif (qt_caracter_w >= 4) then
	qt_caracter_w := 3;
end if;

select	count(*)
into STRICT	qt_nr_ciclo_w
from	cm_ciclo
where	substr(nr_ciclo,(length(nr_ciclo)- qt_caracter_w),length(nr_ciclo)) = nr_ciclo_informado_p
and	nr_seq_equipamento = nr_seq_equipamento_p
and	cd_estabelecimento = cd_estabelecimento_p;

if (qt_nr_ciclo_w > 0) then

	vl_soma_w := (10000 * qt_nr_ciclo_w);
	ds_retorno_p := vl_soma_w + nr_ciclo_informado_p;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
