CREATE OR REPLACE PROCEDURE ajusta_oc_item_reg_compra () AS $body$
DECLARE

 
nr_sequencia_w		bigint;
nr_ordem_compra_w	bigint;

c01 CURSOR FOR 
SELECT	nr_sequencia, 
	lic_obter_oc_reg_compra(nr_seq_licitacao, nr_seq_lic_item,nr_seq_reg_lic_compra) nr_ordem_compra 
from	reg_lic_compra_item 
where	(lic_obter_oc_reg_compra(nr_seq_licitacao, nr_seq_lic_item,nr_seq_reg_lic_compra) IS NOT NULL AND (lic_obter_oc_reg_compra(nr_seq_licitacao, nr_seq_lic_item,nr_seq_reg_lic_compra))::text <> '');
	

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w, 
	nr_ordem_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	update	reg_lic_compra_item 
	set	nr_ordem_compra = nr_ordem_compra_w 
	where	nr_sequencia = nr_sequencia_w;
	end;
end loop;
close C01;
 
 
 
commit;
 
end;
$body$
