CREATE OR REPLACE PROCEDURE ajusta_vol_final_aprox_npt ( ie_tipo_npt_p text, nr_seq_npt_p bigint, nr_sequencia_p bigint, qt_volume_p bigint) AS $body$
BEGIN

case	Upper(ie_tipo_npt_p)
	when 'NPN' then
		update	nut_pac_elemento
		set		qt_volume_final = qt_volume_p,
				ie_editado = 'S'
		where	qt_volume_final <> qt_volume_p
		and		nr_sequencia = nr_sequencia_p
		and		nr_seq_nut_pac	= nr_seq_npt_p;
	when 'NPP' then
		update	nut_pac_elemento
		set		qt_volume_final = qt_volume_p,
				ie_editado = 'S'
		where	qt_volume_final <> qt_volume_p
		and		nr_sequencia = nr_sequencia_p
		and		nr_seq_nut_pac	= nr_seq_npt_p;
end case;

commit;

end;
$body$
