CREATE OR REPLACE PROCEDURE ajusta_w_inserir_processo () AS $body$
DECLARE


c01 CURSOR FOR
SELECT	nr_cot_compra
from	w_inserir_processo;

nr_cot_compra_w		bigint;
cd_estabelecimento_w	bigint;


BEGIN

open C01;
loop
fetch C01 into
	nr_cot_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	coalesce(max(cd_estabelecimento),0)
	into STRICT	cd_estabelecimento_w
	from	cot_compra
	where	nr_cot_compra = nr_cot_compra_w;


	if (cd_estabelecimento_w > 0) then
		update	w_inserir_processo
		set	cd_estabelecimento = cd_estabelecimento_w
		where	nr_cot_compra = nr_cot_compra_w;
	end if;

	end;
end loop;
close C01;

commit;

end;
