CREATE OR REPLACE PROCEDURE ajuste_auditoria_med_proc (nr_seq_matpaci_p bigint, nr_seq_propaci_p bigint) AS $body$
BEGIN

if (nr_seq_matpaci_p <> 0) then
	update	auditoria_matpaci
	set	ie_tipo_auditoria = 'A',
		nr_seq_motivo  = NULL
	where	nr_seq_matpaci = nr_seq_matpaci_p
	and	ie_tipo_auditoria = 'X';
elsif (nr_seq_propaci_p <> 0) then
	update	auditoria_propaci
	set	ie_tipo_auditoria = 'A',
		nr_seq_motivo  = NULL
	where	nr_seq_propaci = nr_seq_propaci_p
	and	ie_tipo_auditoria = 'X';
end if;


commit;

end;
$body$
