SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alerta_atualizacao_resultado () AS $body$
DECLARE


ie_consiste_simultaneo_w	varchar(1);
ds_erro_w			varchar(255);


BEGIN

select	coalesce(max(vl_parametro),'N')
into STRICT	ie_consiste_simultaneo_w
from	funcao_parametro
where	cd_funcao	= 291
and	nr_sequencia	= 56;


if (ie_consiste_simultaneo_w = 'S') then

	CALL gravar_processo_longo('','ALERTA_ATUALIZACAO_RESULTADO',null);

	begin
	SELECT	wheb_mensagem_pck.get_texto(315087) || chr(10) ||
	   	wheb_mensagem_pck.get_texto(281480) || ': ' || osuser
	INTO STRICT	ds_erro_w
	FROM 	v$session
	WHERE	audsid <> (SELECT userenv('sessionid') )
	AND	username = (select username from v$session where audsid = (select userenv('sessionid') ))
	AND	action like 'GERAR_EIS_RESULTADO%';
	exception
	when others then
		ds_erro_w := '';
	end;

	if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '')  then
		CALL gravar_processo_longo('','',null);
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(233455,'DS_ERRO_P='||ds_erro_w);
	end if;

	CALL gravar_processo_longo('','',0);
end if;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE alerta_atualizacao_resultado () FROM PUBLIC;

