CREATE OR REPLACE PROCEDURE alteracao_profissional_escala ( nr_seq_escala_diaria_p bigint, ds_motivo_alteracao_p text, nm_usuario_p text, ie_opcao_p text default 'E') AS $body$
DECLARE


cd_pessoa_fisica_w      	varchar(10);
dt_inicio_w             		timestamp;
dt_fim_w                		timestamp;
ie_feriado_w            	varchar(1);
qt_min_executado_w      	bigint;
cd_cbo_sus_w            	integer;


BEGIN
/*ie_opcao_p
E - Pessoa escala di√°ria
A - Pessoa adicional da escala
*/
if (coalesce(ie_opcao_p,'E') = 'E') then
	begin
	insert	into escala_diaria_alteracao(
		nr_sequencia,
		nr_seq_escala_diaria,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_pessoa_fisica,
		dt_inicio,
		dt_fim,
		ie_feriado,
		qt_min_executado,
		cd_cbo_sus,
		ds_motivo_alteracao )
	SELECT	nextval('escala_diaria_alteracao_seq'),
		nr_seq_escala_diaria_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_pessoa_fisica,
		dt_inicio,
		dt_fim,
		ie_feriado,
		qt_min_executado,
		cd_cbo_sus,
		coalesce(ds_motivo_alteracao_p,wheb_mensagem_pck.get_texto(305731))
	from	escala_diaria
	where	nr_sequencia = nr_seq_escala_diaria_p;
	end;
elsif (coalesce(ie_opcao_p,'E') = 'A') then
	begin
	insert	into escala_diaria_alteracao(
		nr_sequencia,
		nr_seq_escala_diaria,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_pessoa_fisica,
		dt_inicio,
		dt_fim,
		ie_feriado,
		qt_min_executado,
		cd_cbo_sus,
		ds_motivo_alteracao)
	SELECT	nextval('escala_diaria_alteracao_seq'),
		a.nr_sequencia,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		b.cd_pessoa_fisica,
		a.dt_inicio,
		a.dt_fim,
		a.ie_feriado,
		a.qt_min_executado,
		a.cd_cbo_sus,
		coalesce(ds_motivo_alteracao_p,wheb_mensagem_pck.get_texto(305731))
	from	escala_diaria a,
			escala_diaria_adic b
	where	b.nr_seq_escala_diaria = a.nr_sequencia
	and		b.nr_sequencia = nr_seq_escala_diaria_p;
	end;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
