CREATE OR REPLACE PROCEDURE alterar_analisado_procmat_rep ( ie_proc_mat_p text, nr_seq_p text) AS $body$
BEGIN

if (ie_proc_mat_p = 'P') then
update	procedimento_repasse
set	ie_analisado = CASE WHEN coalesce(ie_analisado, 'N')='S' THEN  'N'  ELSE 'S' END
where	nr_sequencia = nr_seq_p;
end if;

if (ie_proc_mat_p = 'M') then
update	material_repasse
set	ie_analisado = CASE WHEN coalesce(ie_analisado, 'N')='S' THEN  'N'  ELSE 'S' END
where	nr_sequencia = nr_seq_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
