CREATE OR REPLACE PROCEDURE alterar_aprovacao_pre_cad_pj ( nr_seq_p bigint, ie_aprovacao_p text ) AS $body$
BEGIN

  if (nr_seq_p IS NOT NULL AND nr_seq_p::text <> '') then
    update pre_pessoa_juridica
       set ie_aprovacao = ie_aprovacao_p
     where nr_sequencia = nr_seq_p;
  end if;

  if ie_aprovacao_p = 'A' then
    CALL efetiva_cad_pj(nr_seq_p);
  end if;

  commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
