SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_autorizacao_cartao ( nr_seq_movimento_p bigint, nr_autorizacao_p text, nm_usuario_p text) AS $body$
DECLARE


dt_integracao_tef_w	timestamp;
count_w			integer;


BEGIN

if (nr_seq_movimento_p IS NOT NULL AND nr_seq_movimento_p::text <> '') then
	begin
	/*se já tiver integração com o TEF não pode alterar*/

	select	dt_integracao_tef
	into STRICT	dt_integracao_tef_w
	from 	movto_cartao_cr
	where   nr_sequencia = nr_seq_movimento_p;

	if (dt_integracao_tef_w IS NOT NULL AND dt_integracao_tef_w::text <> '') then
		--R.aise_application_error(-20011,'Este movimento já está integrado ao TEF, não pode ser alterado.');
		CALL wheb_mensagem_pck.exibir_mensagem_abort(241433);
	end if;

	/*se já tiver baixas não pode alterar*/

	select 	count(*)
	into STRICT 	count_w
	from	movto_cartao_cr_baixa a
	where	a.nr_seq_movto = nr_seq_movimento_p;

	if ( coalesce(count_w,0) > 0) then
		--R.aise_application_error(-20011,'Este movimento já possui baixas, não pode ser alterado.');
		CALL wheb_mensagem_pck.exibir_mensagem_abort(241434);
	end if;

	update	movto_cartao_cr
	set	nr_autorizacao	= nr_autorizacao_p
	where	nr_sequencia   	= nr_seq_movimento_p;

	end;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
