CREATE OR REPLACE PROCEDURE alterar_bloqueto_titulo ( nr_bloqueto_p text, nr_titulo_p bigint) AS $body$
BEGIN
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
	begin
	update 	titulo_pagar
	set 	nr_bloqueto = nr_bloqueto_p
	where 	nr_titulo = nr_titulo_p;
	if (coalesce(nr_bloqueto_p::text, '') = '') then
		begin
		update 	titulo_pagar
		set 	vl_bloq_associado  = NULL
		where 	nr_titulo = nr_titulo_p;
		end;
	end if;
	commit;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
