CREATE OR REPLACE PROCEDURE alterar_cgc_mat_autorizado ( cd_cgc_p text, nr_seq_mat_autor_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_mat_autor_p IS NOT NULL AND nr_seq_mat_autor_p::text <> '') then
	update	material_autorizado
	set    	dt_atualizacao       = clock_timestamp(),
		cd_cgc_fabricante    = cd_cgc_p,
		nm_usuario           = nm_usuario_p,
		qt_solicitada        = 0
	where  	nr_sequencia         = nr_seq_mat_autor_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
