CREATE OR REPLACE PROCEDURE alterar_cheque_cr_portador ( cd_tipo_portador_p bigint, cd_portador_p bigint, nr_seq_cheque_p bigint, nm_usuario_p text) AS $body$
DECLARE

					 
ie_cheque_cobranca_w	portador.ie_cheque_cobranca%type;
					

BEGIN 
 
if (cd_tipo_portador_p IS NOT NULL AND cd_tipo_portador_p::text <> '') and (cd_portador_p IS NOT NULL AND cd_portador_p::text <> '') and (nr_seq_cheque_p IS NOT NULL AND nr_seq_cheque_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	update cheque_cr 
	set   cd_tipo_portador = cd_tipo_portador_p, 
		cd_portador    = cd_portador_p, 
		nm_usuario    = nm_usuario_p, 
		dt_atualizacao 	 = clock_timestamp()	 
	where  nr_seq_cheque   = nr_seq_cheque_p;
 
	update 	cobranca 
	set  	cd_tipo_portador = cd_tipo_portador_p, 
		cd_portador    = cd_portador_p, 
		nm_usuario    = nm_usuario_p, 
		dt_atualizacao 	 = clock_timestamp()	 
	where 	nr_seq_cheque   = nr_seq_cheque_p;
	 
	select	coalesce(max(a.ie_cheque_cobranca),'N') 
	into STRICT	ie_cheque_cobranca_w 
	from	portador a 
	where	a.cd_portador = cd_portador_p;
	 
	if (coalesce(ie_cheque_cobranca_w,'N') = 'S') then 
		CALL atualizar_cobranca_cheque(nr_seq_cheque_p,nm_usuario_p);
	end if;
	 
	end;
end if;
commit;
 
end;
$body$
