SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_ciencia_conformidade ( nr_prescricao_p prescr_medica.nr_prescricao%type, dt_confirmacao_p timestamp, nm_usuario_ciente_p usuario.nm_usuario%type, ds_justificativa_p text, nr_sequencia_p bigint default null, ie_cpoe_p text default 'N', ie_origem_informacao_p text default 'T', ie_quimio_p text default 'N') AS $body$
DECLARE


/*
   ie_cpoe_p = S
   ie_origem_informacao_p = T/MDX/LXC (Tasy/Micromedex/Lexicomp)
*/
	qt_riscos_medic_w	bigint;


BEGIN
	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_cpoe_p = 'S') then
		if (coalesce(ie_origem_informacao_p, 'T') = 'T') then
			update cpoe_justificativa_item
			set    dt_confirmacao = dt_confirmacao_p,
				   nm_usuario_ciente = nm_usuario_ciente_p,
				   ds_motivo = ds_justificativa_p,
				   ie_ciente = 'S'
			where  nr_sequencia = nr_sequencia_p
			  and  coalesce(ie_ciente, 'N') = 'N';
		elsif (coalesce(ie_origem_informacao_p, 'T') = 'MDX') then
			update cpoe_justif_interacao
			set    dt_confirmacao = dt_confirmacao_p,
				   nm_usuario_ciente = nm_usuario_ciente_p,
				   ds_motivo = ds_justificativa_p,
				   ie_ciente = 'S'
			where  nr_sequencia = nr_sequencia_p
			  and  coalesce(ie_ciente, 'N') = 'N';
		elsif (coalesce(ie_origem_informacao_p, 'T') = 'LXC') then
			update alerta_api
			set    dt_confirmacao = dt_confirmacao_p,
				   nm_usuario_ciente = nm_usuario_ciente_p,
				   ds_motivo = ds_justificativa_p,
				   ie_ciente = 'S'
			where  nr_sequencia = nr_sequencia_p
			  and  coalesce(ie_ciente, 'N') = 'N';
		end if;
	elsif (ie_quimio_p = 'S' and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '')) then

		update	paciente_atend_erro_just
		set		dt_confirmacao = dt_confirmacao_p,
				nm_usuario_ciente = nm_usuario_ciente_p,
				ds_motivo = ds_justificativa_p,
				ie_ciente = 'S'
		where	nr_sequencia = nr_sequencia_p;

	else
		update prescr_medica_erro
		set  dt_confirmacao = dt_confirmacao_p,
			   nm_usuario_ciente = nm_usuario_ciente_p,
			   ds_motivo = ds_justificativa_p,
			   ie_ciente = 'S'
		where nr_prescricao = nr_prescricao_p
		and     ie_agrupador in (1,2,5)
		and     coalesce(ie_ciente,'N') = 'N';
		
		select 	count(1)
		into STRICT	qt_riscos_medic_w
		from 	prescr_medica_risco
		where 	nr_prescricao = nr_prescricao_p
		and		coalesce(ie_ciente,'N') = 'N'
		and		coalesce(dt_confirmacao::text, '') = '';
		
		if (coalesce(qt_riscos_medic_w, 0) > 0) then
			update 	prescr_medica_risco
			set  	dt_confirmacao 		= dt_confirmacao_p,
					nm_usuario_ciente 	= nm_usuario_ciente_p,
					ds_motivo 			= ds_justificativa_p,
					ie_ciente 			= 'S'
			where 	nr_prescricao 		= nr_prescricao_p
			and     coalesce(ie_ciente,'N') 	= 'N';
		end if;
	end if;

	commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
