SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_classifi_diag_doenca (nr_seq_interno_p bigint, nm_usuario_p text, ie_diag_tratamento_p text, ie_diag_referencia_p text, ie_diag_princ_episodio_p text, ie_diag_princ_depart_p text, ie_diag_pre_cir_p text, ie_diag_obito_p text, ie_diag_cirurgia_p text, ie_diag_alta_p text, ie_diag_admissao_p text, ie_diag_trat_cert_p text, ie_tipo_diagnostico_p text, ie_classificacao_doenca_p text) AS $body$
DECLARE


dt_diagnostico_w		diagnostico_doenca_depart.dt_diagnostico%type;
cd_doenca_w 			diagnostico_doenca_depart.cd_doenca%type;
nr_atendimento_w  		diagnostico_doenca_depart.nr_atendimento%type;
cd_setor_atendimento_w	diagnostico_doenca_depart.cd_setor_atendimento%type;
cd_profissional_w		diagnostico_doenca_depart.cd_profissional%type;
cd_pessoa_fisica_w		diagnostico_doenca_depart.cd_pessoa_fisica%type;
dt_atualizacao_nrec_w	diagnostico_doenca_depart.dt_atualizacao_nrec%type;
nr_seq_interno_w		diagnostico_doenca_depart.nr_seq_interno%type;
dt_alta_w				atendimento_paciente.dt_alta%type;


BEGIN
	
	select 	dt_diagnostico,
			cd_doenca,
			nr_atendimento,
			cd_profissional,
			cd_setor_atendimento,
			obter_pessoa_atendimento(nr_atendimento, 'C'),
			dt_atualizacao_nrec,
			nr_seq_interno
	into STRICT 	dt_diagnostico_w,
			cd_doenca_w,
			nr_atendimento_w,
			cd_profissional_w,
			cd_setor_atendimento_w,
			cd_pessoa_fisica_w,
			dt_atualizacao_nrec_w,
			nr_seq_interno_w
	from	diagnostico_doenca_depart
	where	nr_sequencia = nr_seq_interno_p;	

	insert into diagnostico_doenca_depart(
		nr_sequencia,
		dt_diagnostico,
		cd_doenca,
		nr_atendimento,
		cd_profissional,
		cd_pessoa_fisica,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		dt_atualizacao,
		dt_registro,
		cd_setor_atendimento,
		ie_diag_referencia,
		ie_diag_tratamento,
		ie_diag_admissao,
		ie_diag_trat_cert,
		ie_diag_pre_cir,
		ie_diag_cirurgia,
		ie_diag_alta,
		ie_diag_obito,
		ie_diag_princ_depart,
		ie_diag_princ_episodio,
		nm_usuario,
		ie_situacao,
		nr_seq_episodio,
		nr_seq_interno,
		ie_tipo_diagnostico,
		ie_classificacao_doenca
	) values (
		nextval('diagnostico_doenca_depart_seq'),
		dt_diagnostico_w,
		cd_doenca_w,
		nr_atendimento_w,
		cd_profissional_w,
		cd_pessoa_fisica_w,
		nm_usuario_p,
		dt_atualizacao_nrec_w,
		clock_timestamp(),
		clock_timestamp(),
		cd_setor_atendimento_w,
		ie_diag_referencia_p,
		ie_diag_tratamento_p,
		ie_diag_admissao_p,
		ie_diag_trat_cert_p,
		ie_diag_pre_cir_p,
		ie_diag_cirurgia_p,
		ie_diag_alta_p,
		ie_diag_obito_p,
		ie_diag_princ_depart_p,
		ie_diag_princ_episodio_p,
		nm_usuario_p,
		'A',
		CASE WHEN obter_episodio_atendimento(nr_atendimento_w)=0 THEN null  ELSE obter_episodio_atendimento(nr_atendimento_w) END ,
		nr_seq_interno_w,
		ie_tipo_diagnostico_p,
		ie_classificacao_doenca_p
	);
	
	select	max(dt_alta)
	into STRICT	dt_alta_w
	from	atendimento_paciente
	where	nr_atendimento = nr_atendimento_w;

	
	if (obter_departamento_data_diag(nr_atendimento_w, dt_atualizacao_nrec_w) = obter_departamento_data(nr_atendimento_w, clock_timestamp())) or	
		((dt_alta_w IS NOT NULL AND dt_alta_w::text <> '') and (obter_departamento_data_diag(nr_atendimento_w, dt_atualizacao_nrec_w) = obter_departamento_setor(obter_ultimo_setor_atendimento(nr_atendimento_w))))	then
		
		update diagnostico_doenca
		set dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p,
			ie_diag_referencia = ie_diag_referencia_p,		
			ie_diag_tratamento = ie_diag_tratamento_p,
			ie_diag_admissao = ie_diag_admissao_p,
			ie_diag_trat_cert = ie_diag_trat_cert_p,
			ie_diag_pre_cir = ie_diag_pre_cir_p,
			ie_diag_cirurgia = ie_diag_cirurgia_p,
			ie_diag_alta = ie_diag_alta_p,
			ie_diag_obito = ie_diag_obito_p,
			ie_diag_princ_depart = ie_diag_princ_depart_p,
			ie_diag_princ_episodio = ie_diag_princ_episodio_p
		where nr_seq_interno = nr_seq_interno_w;
	end if;
	
	update	diagnostico_doenca_depart
	set		ie_situacao = 'I'
	where	nr_sequencia = nr_seq_interno_p;
	
	commit;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
