SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_cliente_regra ( nr_seq_regra_p bigint, nr_seq_cliente_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_cliente_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_cliente_w
from	com_cliente a
where	a.nr_sequencia	= coalesce(nr_seq_cliente_p,0);

if (coalesce(qt_cliente_w,0) = 0) then
	/* Não foi localizado nenhuma cliente com a sequência nr_seq_cliente_p! */

	CALL wheb_mensagem_pck.exibir_mensagem_abort(270637,'NR_SEQ_CLIENTE_P='||nr_seq_cliente_p);
end if;

update	com_cliente_regra_valor
set	nr_seq_cliente	= nr_seq_cliente_p,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_regra_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
