SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_codigo_contrato ( nr_seq_contrato_p bigint, cd_contrato_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE



ds_historico_w			varchar(4000)	:= '';
nm_pessoa_alteracao_w		varchar(255);
cd_contrato_ant_w		varchar(255);
ds_titulo_w			varchar(255);
ie_atualiza_aditivos_w		varchar(1);
nr_seq_contrato_ad_w		bigint;

c01 CURSOR FOR
SELECT	nr_sequencia
from	contrato
where	nr_seq_contrato_atual = nr_seq_contrato_p;


BEGIN

nm_pessoa_alteracao_w	:=  substr(obter_nome_usuario(nm_usuario_p),1,255);

select	max(cd_contrato)
into STRICT	cd_contrato_ant_w
from	contrato
where	nr_Sequencia	= nr_seq_contrato_p;


update	contrato
set	cd_contrato	= cd_contrato_p,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_contrato_p;

ds_titulo_w	:=	wheb_mensagem_pck.get_texto(315089);

ds_historico_w	:=	substr(wheb_mensagem_pck.get_texto(315090, 	'NM_PESSOA_ALTERACAO_W='||NM_PESSOA_ALTERACAO_W||
									';NM_USUARIO_P='||NM_USUARIO_P||
									';CD_CONTRATO_ANT_W='|| coalesce(CD_CONTRATO_ANT_W,wheb_mensagem_pck.get_texto(166761))||
									';CD_CONTRATO_P='||CD_CONTRATO_ANT_W),1,2000);

insert into contrato_historico(
	nr_sequencia,
	nr_seq_contrato,
	dt_historico,
	ds_historico,
	dt_atualizacao,
	nm_usuario,
	ds_titulo,
	dt_liberacao,
	nm_usuario_lib)
values (	nextval('contrato_historico_seq'),
	nr_seq_contrato_p,
	clock_timestamp(),
	ds_historico_w,
	clock_timestamp(),
	nm_usuario_p,
	ds_titulo_w,
	clock_timestamp(),
	nm_usuario_p);

ie_atualiza_aditivos_w	:= coalesce((obter_valor_param_usuario(1200, 110, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p)), 'N');

if (ie_atualiza_aditivos_w = 'S') then
	begin

	open c01;
	loop
	fetch c01 into
		nr_seq_contrato_ad_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		update	contrato
		set	cd_contrato	= cd_contrato_p,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= nr_seq_contrato_ad_w;

		insert into contrato_historico(
			nr_sequencia,
			nr_seq_contrato,
			dt_historico,
			ds_historico,
			dt_atualizacao,
			nm_usuario,
			ds_titulo,
			dt_liberacao,
			nm_usuario_lib)
		values (	nextval('contrato_historico_seq'),
			nr_seq_contrato_ad_w,
			clock_timestamp(),
			ds_historico_w,
			clock_timestamp(),
			nm_usuario_p,
			ds_titulo_w,
			clock_timestamp(),
			nm_usuario_p);

		end;
	end loop;
	close c01;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
