CREATE OR REPLACE PROCEDURE alterar_codigo_retencao ( cd_retencao_p text, nr_sequencia_p bigint, ie_pasta_selec_p text) AS $body$
BEGIN
if (cd_retencao_p IS NOT NULL AND cd_retencao_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	-- Executa update caso a pasta selecionada seja TituloWJP
	if (ie_pasta_selec_p = 'S') then
		begin
		update 	titulo_pagar
		set    	cd_darf =  cd_retencao_p
		where 	nr_titulo = nr_sequencia_p;
		end;
	else
	-- Executa update caso a pasta selecionada seja ImpostoTituloWJP
		begin
		update 	titulo_pagar_imposto
		set    	cd_darf = cd_retencao_p
		where 	nr_sequencia = nr_sequencia_p;
		end;
	end if;
	commit;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
