CREATE OR REPLACE PROCEDURE alterar_complemento_item_nota ( nm_usuario_p text, nr_seq_nota_p bigint, nr_item_nf_p bigint, ds_complemento_p text) AS $body$
DECLARE


ds_complemento_w 	varchar(255);
cd_material_w		integer;
ds_material_w		varchar(255);
historico_w		varchar(2000);


BEGIN

if (nr_item_nf_p IS NOT NULL AND nr_item_nf_p::text <> '') then


	select 	ds_complemento,
		cd_material,
		substr(obter_desc_material(cd_material),1,255) ds_material
	into STRICT 	ds_complemento_w,
		cd_material_w,
		ds_material_w
	from	nota_fiscal_item
	where 	nr_sequencia	= nr_seq_nota_p
	and	nr_item_nf	= nr_item_nf_p;

	update	nota_fiscal_item
	set	ds_complemento	= ds_complemento_p
	where	nr_sequencia	= nr_seq_nota_p
	and 	nr_item_nf	= nr_item_nf_p;

	historico_w := 	substr(wheb_mensagem_pck.get_texto(1060636) || ' [' || nr_item_nf_p || '] ' || cd_material_w || ' (' || ds_material_w || '). ' ||
			wheb_mensagem_pck.get_texto(310560)|| ' ' ||  substr(ds_complemento_w,1,255) || chr(13) || chr(10) ||
			wheb_mensagem_pck.get_texto(280393)||' ' ||  substr(ds_complemento_p,1,255),1,2000);

	CALL gerar_historico_nota_fiscal(
		nr_seq_nota_p,
		nm_usuario_p,
		34,
		historico_w);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
