CREATE OR REPLACE PROCEDURE alterar_cota_lista_espera ( nr_seq_agen_int_contrato_p bigint) AS $body$
DECLARE


qt_cota_w	bigint;

BEGIN

if (coalesce(nr_seq_agen_int_contrato_p,0) > 0)then

	select	max(qt_cota_atual)
	into STRICT 	qt_cota_w
	from 	agenda_integrada_contrato 
	where	nr_sequencia = nr_seq_agen_int_contrato_p;

	if (coalesce(qt_cota_w,0) > 0)then

		qt_cota_w := qt_cota_w - 1;

		update	agenda_integrada_contrato
		set 	qt_cota_atual = qt_cota_w
		where 	nr_sequencia = nr_seq_agen_int_contrato_p;

	end if;

end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
