CREATE OR REPLACE PROCEDURE alterar_dados_itens ( nr_seq_hist_item_p bigint, cd_motivo_glosa_p bigint, ie_acao_glosa_p text, ds_observacao_p text, nm_usuario_p text, ds_justificativa_p text) AS $body$
BEGIN

if (coalesce(cd_motivo_glosa_p,0) > 0) then
	update	convenio_retorno_glosa
	set	dt_atualizacao		= clock_timestamp(),
		nm_usuario			= nm_usuario_p,
		cd_motivo_glosa		= cd_motivo_glosa_p
	where	nr_sequencia	= nr_seq_hist_item_p;
end if;

if (coalesce(ie_acao_glosa_p,'X') <> 'X') then
	update	convenio_retorno_glosa
	set	dt_atualizacao		= clock_timestamp(),
		nm_usuario			= nm_usuario_p,
		ie_acao_glosa		= ie_acao_glosa_p
	where	nr_sequencia	= nr_seq_hist_item_p;
end if;

if (ds_justificativa_p <> '') then
	update	convenio_retorno_glosa
	set	dt_atualizacao		= clock_timestamp(),
		nm_usuario			= nm_usuario_p,
		ds_justificativa	= ds_justificativa_p
	where	nr_sequencia	= nr_seq_hist_item_p;
end if;

if (ds_observacao_p <> '') then
	update	convenio_retorno_glosa
	set	dt_atualizacao		= clock_timestamp(),
		nm_usuario			= nm_usuario_p,
		ds_observacao	= ds_observacao_p
	where	nr_sequencia	= nr_seq_hist_item_p;
end if;

commit;

end;
