CREATE OR REPLACE PROCEDURE alterar_dados_painel_html ( nr_seq_agenda_p bigint, nr_seq_sala_cir_p bigint, hr_revisada_p timestamp, ds_observacao_p text, nm_usuario_p text, ds_inf_painel_p text, ie_visita_pre_anest_p text, ie_opcao_p text) AS $body$
DECLARE


ds_historico_w			varchar(2000);
nr_atendimento_w		bigint;
nr_cirurgia_w			bigint;
ds_sala_antiga_w		varchar(255);
ds_sala_nova_w			varchar(255);
hr_inicio_w timestamp;
hr_revisada_w timestamp;


BEGIN

select max(hr_inicio)
into STRICT hr_inicio_w
from agenda_paciente
where nr_sequencia = nr_seq_agenda_p;

if (to_char(hr_revisada_p, 'yyyy') = '1970') then
  hr_revisada_w := trunc(hr_inicio_w) + (hr_revisada_p - trunc(hr_revisada_p));
end if;

if (ie_opcao_p = 'P') then
	select	max(nr_atendimento),
				max(nr_cirurgia)
	into STRICT		nr_atendimento_w,
				nr_cirurgia_w
	from		agenda_paciente
	where		nr_sequencia = nr_seq_agenda_p;

	select   max(a.ds_sala_painel)
	into STRICT		ds_sala_antiga_w
	from 		sala_cirurgia a,
				agenda_paciente b
	where 	b.nr_seq_sala_cir 	= a.nr_sequencia
	and		b.nr_sequencia 		= nr_seq_agenda_p;

	SELECT   max(a.ds_sala_painel)
	into STRICT		ds_sala_nova_w
	FROM 		sala_cirurgia a
	where 	nr_sequencia = nr_seq_sala_cir_p;

	CALL alterar_dados_painel(nr_seq_agenda_p,nr_seq_sala_cir_p,hr_revisada_w,ds_observacao_p,nm_usuario_p,ds_inf_painel_p,ie_visita_pre_anest_p);

	if (coalesce(ds_sala_antiga_w::text, '') = '') then
		ds_historico_w	:= substr(obter_texto_dic_objeto(354989,wheb_usuario_pck.get_nr_seq_idioma,'DS_AGENDA_ATUAL=' ||ds_sala_nova_w||';'||'NR_AGENDA_ANTIGA='||nr_seq_agenda_p||';'),1,2000);
	else
		ds_historico_w	:= substr(obter_texto_dic_objeto(354990,wheb_usuario_pck.get_nr_seq_idioma,'NR_AGENDA_ANTIGA=' ||nr_seq_agenda_p||';'||'DS_AGENDA_ANTIGA='||ds_sala_antiga_w||';'||'DS_AGENDA_ATUAL='||ds_sala_nova_w||';'),1,2000);
	end if;
	CALL gravar_historico_identific_pac(nr_atendimento_w,nr_cirurgia_w,'IP',ds_historico_w,nm_usuario_p);
else
	CALL alterar_dados_painel(nr_seq_agenda_p,nr_seq_sala_cir_p,hr_revisada_w,ds_observacao_p,nm_usuario_p,ds_inf_painel_p,ie_visita_pre_anest_p);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
