CREATE OR REPLACE PROCEDURE alterar_dados_painel ( nr_seq_agenda_p bigint, nr_seq_sala_cir_p bigint, hr_revisada_p timestamp, ds_observacao_p text, nm_usuario_p text, ds_inf_painel_p text, ie_visita_pre_anest_p text) AS $body$
DECLARE

nr_seq_interno_w	bigint;

BEGIN

if (nr_seq_agenda_p > 0) then
	select 	max(nr_seq_interno)
	into STRICT	nr_seq_interno_w
	from	sala_cirurgia
	where	nr_sequencia = nr_seq_sala_cir_p;

	update	agenda_paciente
	set	nr_seq_sala_cir	=	nr_seq_sala_cir_p,
		hr_revisada	=	hr_revisada_p,
		ds_observacao	=	ds_observacao_p,
		nm_usuario	=	nm_usuario_p,
		dt_atualizacao	=	clock_timestamp(),
		ds_inf_painel   =	ds_inf_painel_p,
		dt_visita_pre_anest =   CASE WHEN ie_visita_pre_anest_p='S' THEN clock_timestamp()  ELSE null END ,
		nr_seq_interno_sala_cir	= nr_seq_interno_w
	where	nr_sequencia	=	nr_seq_agenda_p;
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
