CREATE OR REPLACE PROCEDURE alterar_dados_prescr ( cd_senha_p text, nr_doc_conv_p text, nr_prescricao_p bigint, ie_imprime_p text, nm_usuario_p text, ie_limpa_guia_p text, ie_limpa_senha_p text) AS $body$
DECLARE


dt_alta_w	timestamp;
nr_doc_conv_w	varchar(20) := nr_doc_conv_p;


BEGIN


if (cd_senha_p IS NOT NULL AND cd_senha_p::text <> '')
or (ie_limpa_senha_p = 'S') then

	update 	prescr_medica
	set 	cd_senha      = cd_senha_p,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
	where 	nr_prescricao = nr_prescricao_p;

	if 	((ie_imprime_p = 'S') or (ie_imprime_p = 'H')) then
		update 	procedimento_paciente
		set 	cd_senha      = cd_senha_p,
				nm_usuario		= nm_usuario_p,
				dt_atualizacao		= clock_timestamp()
		where 	nr_prescricao = nr_prescricao_p;
	end if;
end if;

if (nr_doc_conv_w IS NOT NULL AND nr_doc_conv_w::text <> '')
or (ie_limpa_guia_p = 'S') then

	update 	prescr_medica
	set 	nr_doc_conv   = nr_doc_conv_w,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
	where 	nr_prescricao = nr_prescricao_p;

	if (ie_imprime_p = 'S') then
		update 	procedimento_paciente
		set 	nr_doc_convenio = nr_doc_conv_w,
				nm_usuario		= nm_usuario_p,
				dt_atualizacao		= clock_timestamp()
		where 	nr_prescricao   = nr_prescricao_p;
	elsif (ie_imprime_p = 'H') then
		update 	procedimento_paciente
		set 	nr_doc_honor_conv  = nr_doc_conv_w,
				nr_doc_convenio 	 = nr_doc_conv_w,
				nm_usuario		= nm_usuario_p,
				dt_atualizacao		= clock_timestamp()
		where 	nr_prescricao 	 = nr_prescricao_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
