CREATE OR REPLACE PROCEDURE alterar_danfe_nf ( nr_nota_fiscal_p bigint, nr_danfe_p text) AS $body$
BEGIN
if (nr_nota_fiscal_p IS NOT NULL AND nr_nota_fiscal_p::text <> '') and (nr_danfe_p IS NOT NULL AND nr_danfe_p::text <> '') then
	begin

	update	nota_fiscal
	set	nr_danfe	= substr(nr_danfe_p,1,60)
	where	nr_sequencia	= nr_nota_fiscal_p;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
