CREATE OR REPLACE PROCEDURE alterar_data_evento_fanep ( dt_inicio_p timestamp, dt_fim_p timestamp, nr_sequencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

					 
nr_prescricao_w		bigint;					
 

BEGIN 
if (dt_inicio_p IS NOT NULL AND dt_inicio_p::text <> '') then 
	update	pepo_cirurgia 
	set	dt_inicio_proced 	= dt_inicio_p, 
		nm_usuario 		= nm_usuario_p 
	where	nr_sequencia 		= nr_sequencia_p;
	 
	select	max(nr_prescricao) 
	into STRICT	nr_prescricao_w 
	from	pepo_cirurgia 
	where	nr_sequencia = nr_sequencia_p;
	if (coalesce(nr_prescricao_w::text, '') = '') then 
		nr_prescricao_w := Gerar_prescr_Fanep(cd_estabelecimento_p, nr_sequencia_p, nm_usuario_p, nr_prescricao_w);
		if (coalesce(nr_prescricao_w,0) > 0) then 
			update	pepo_cirurgia 
			set	nr_prescricao		= nr_prescricao_w, 
				nm_usuario 		= nm_usuario_p 
			where	nr_sequencia 		= nr_sequencia_p;
		end if;
	end if;	
end if;
 
if (dt_fim_p IS NOT NULL AND dt_fim_p::text <> '') then 
	update	pepo_cirurgia 
	set	dt_fim_cirurgia	= dt_fim_p, 
		nm_usuario 	= nm_usuario_p 
	where	nr_sequencia 	= nr_sequencia_p;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
