CREATE OR REPLACE PROCEDURE alterar_data_referencia_prot ( nr_sequencia_p bigint, dt_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE

			 
nr_sequencia_w	varchar(10);

C01 CURSOR FOR 
	SELECT 	a.nr_seq_protocolo 
	from 	protocolo_convenio a, 
		lote_protocolo   b 
	where 	a.nr_seq_lote_protocolo = b.nr_sequencia 
	and  	b.nr_sequencia = nr_sequencia_p;
		

BEGIN 
 
update lote_protocolo 
set	dt_mesano_referencia = dt_referencia_p 
where	nr_sequencia = nr_sequencia_p;
 
 
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
		CALL Atualizar_Ref_Protocolo_Conv(nr_sequencia_w, 
		dt_referencia_p, 
		nm_usuario_p, 
		wheb_usuario_pck.get_cd_estabelecimento, 
		'');
	end;
end loop;
close C01;	
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
