CREATE OR REPLACE PROCEDURE alterar_data_solicitacao ( nr_solic_compra_p bigint, dt_solic_comp_alt_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
ds_hist_w			varchar(2000);
dt_solicitacao_compra_w		timestamp;


BEGIN 
 
if (nr_solic_compra_p IS NOT NULL AND nr_solic_compra_p::text <> '') then 
	 
	select	dt_solicitacao_compra 
	into STRICT	dt_solicitacao_compra_w 
	from	solic_compra 
	where	nr_solic_compra = nr_solic_compra_p;
	 
	update	solic_compra 
	set	dt_solicitacao_compra = dt_solic_comp_alt_p 
	where	nr_solic_compra = nr_solic_compra_p;
					 
	ds_hist_w := substr(	WHEB_MENSAGEM_PCK.get_texto(297022,'DT_SOLICITACAO_COMPRA_W=' || dt_solicitacao_compra_w || ';' || 
							'DT_SOLIC_ACOMP_ALT_P=' || dt_solicitacao_compra_w),1,2000);
					 
	CALL gerar_historico_solic_compra(	nr_solic_compra_p, 
					WHEB_MENSAGEM_PCK.get_texto(297026), 
					ds_hist_w, 
					'ADT', 
					nm_usuario_p);
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
