CREATE OR REPLACE PROCEDURE alterar_decisor_clinico ( cd_pessoa_fisica_p text, cd_agente_saude_p text, nm_usuario_p text) AS $body$
DECLARE


ie_possui_aux_w 	varchar(1);


BEGIN

select CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
into STRICT ie_possui_aux_w
from pessoa_fisica_aux
where cd_pessoa_fisica = cd_pessoa_fisica_p;

if (ie_possui_aux_w = 'S') then

	update 	pessoa_fisica_aux
	set 	cd_agente_saude = cd_agente_saude_p,
		nm_usuario_nrec = nm_usuario_p,
		dt_atualizacao_nrec = clock_timestamp()
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;
else

	insert into pessoa_fisica_aux(
		nr_sequencia,
		cd_pessoa_fisica,
		cd_agente_saude,
		dt_atualizacao,
		dt_atualizacao_nrec,
		nm_usuario,
		nm_usuario_nrec)
	values (
		nextval('pessoa_fisica_aux_seq'),
		cd_pessoa_fisica_p,
		cd_agente_saude_p,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
