CREATE OR REPLACE PROCEDURE alterar_dest_gasto_item ( nr_sequencia_p bigint, ie_trat_conta_rn_p text, ie_tipo_item_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ie_trat_conta_rn_w			varchar(15) :='0';
cd_convenio_w				integer;									

BEGIN 
 
if (ie_tipo_item_p = 1) then 
 
	select	coalesce(max(ie_trat_conta_rn),'0'), 
		coalesce(max(cd_convenio),'0') 
	into STRICT	ie_trat_conta_rn_w, 
		cd_convenio_w 
	from	procedimento_paciente 
	where	nr_sequencia = nr_sequencia_p;
 
	if (ie_trat_conta_rn_w <> '0') then 
		update	procedimento_paciente 
		set		ie_trat_conta_rn = ie_trat_conta_rn_p 
		where	nr_sequencia 	 = nr_sequencia_p;
	end if;
	 
	CALL atualiza_preco_procedimento(nr_sequencia_p, cd_convenio_w,nm_usuario_p);
	 
else 
	select	coalesce(max(ie_trat_conta_rn),'0') 
	into STRICT	ie_trat_conta_rn_w 
	from	material_atend_paciente 
	where	nr_sequencia = nr_sequencia_p;
 
	if (ie_trat_conta_rn_w <> '0') then 
		update	material_atend_paciente 
		set		ie_trat_conta_rn = ie_trat_conta_rn_p 
		where	nr_sequencia 	 = nr_sequencia_p;
	end if;
	 
	CALL atualiza_preco_material(nr_sequencia_p, nm_usuario_P);
	 
	 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
