SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_dilue_trat_onc ( nr_seq_paciente_p bigint, nr_seq_interno_p bigint, ie_operacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_int_medic_w	bigint;
nr_seq_atendimento_w	bigint;
nr_seq_material_w	bigint;
nr_seq_mat_dil_w	bigint;
nr_seq_interno_w	bigint;
cd_material_w		bigint;
nr_agrupamento_w	bigint;

C01 CURSOR FOR
	SELECT	*
	from	paciente_protocolo_medic
	where	nr_seq_paciente = nr_seq_paciente_p
	and	nr_seq_interno = nr_seq_interno_p;

c01_w	c01%rowtype;

C02 CURSOR FOR
	SELECT	nr_seq_atendimento,
		nr_seq_material
	from	paciente_atend_medic
	where	cd_material = cd_material_w
	and	nr_agrupamento = nr_agrupamento_w
	and	nr_seq_paciente = nr_seq_paciente_p;


BEGIN

	open C01;
	loop
	fetch C01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select	max(cd_material),
			max(nr_agrupamento)
		into STRICT	cd_material_w,
			nr_agrupamento_w
		from	paciente_protocolo_medic
		where	nr_seq_paciente = nr_seq_paciente_p
		and	nr_seq_material = c01_w.nr_seq_diluicao;


		open C02;
		loop
		fetch C02 into
			nr_seq_atendimento_w,
			nr_seq_material_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin

			if (ie_operacao_p = 3) then
				delete
				from	paciente_atend_medic
				where	nr_seq_atendimento = nr_seq_atendimento_w
				and	cd_material = c01_w.cd_material
				and	nr_agrupamento = c01_w.nr_agrupamento;

				commit;
			end if;

			select	coalesce(max(nr_seq_material),0) + 1
			into STRICT	nr_seq_mat_dil_w
			from	paciente_atend_medic
			where	nr_seq_atendimento = nr_seq_atendimento_w;

			select	nextval('paciente_atend_medic_seq')
			into STRICT	nr_seq_interno_w
			;

			if (ie_operacao_p = 1) then

			insert into paciente_atend_medic(
					nr_seq_atendimento,
					nr_seq_material,
					cd_material,
					dt_atualizacao,
					nm_usuario,
					nr_agrupamento,
					qt_dose,
					cd_unid_med_dose,
					ds_recomendacao,
					ds_observacao,
					ie_via_aplicacao,
					nr_seq_diluicao,
					qt_min_aplicacao,
					ie_bomba_infusao,
					cd_intervalo,
					qt_hora_aplicacao,
					qt_dias_util,
					nr_seq_interno,
					nr_seq_prot_medic,
					ie_cancelada,
					ie_administracao,
					ie_checado,
					ie_se_necessario,
					ie_urgencia,
					ie_aplic_bolus,
					ie_aplic_lenta,
					nr_seq_int_prot_medic,
					ie_agrupador,
					ie_aplica_reducao,
					ie_local_adm,
					cd_unid_med_prescr,
					qt_dose_prescricao,
					ie_medicacao_paciente)
				values (	nr_seq_atendimento_w,
					nr_seq_mat_dil_w,
					c01_w.cd_material,
					clock_timestamp(),
					nm_usuario_p,
					c01_w.nr_agrupamento,
					c01_w.qt_dose,
					c01_w.cd_unidade_medida,
					c01_w.ds_recomendacao,
					c01_w.ds_observacao,
					c01_w.ie_via_aplicacao,
					nr_seq_material_w,
					c01_w.qt_min_aplicacao,
					c01_w.ie_bomba_infusao,
					c01_w.cd_intervalo,
					c01_w.qt_hora_aplicacao,
					c01_w.qt_dias_util,
					nr_seq_interno_w,
					c01_w.nr_seq_interno,
					'N',
					'P',
					'N',
					c01_w.ie_se_necessario,
					c01_w.ie_urgencia,
					c01_w.ie_aplic_bolus,
					c01_w.ie_aplic_lenta,
					c01_w.nr_seq_interno,
					c01_w.ie_agrupador,
					c01_w.ie_aplica_reducao,
					c01_w.ie_local_adm,
					c01_w.cd_unid_med_prescr,
					c01_w.qt_dose_prescr,
					coalesce(c01_w.ie_medicacao_paciente,'N'));
			end if;

			end;
		end loop;
		close C02;




		end;
	end loop;
	close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE alterar_dilue_trat_onc ( nr_seq_paciente_p bigint, nr_seq_interno_p bigint, ie_operacao_p bigint, nm_usuario_p text) FROM PUBLIC;

