SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_dispositivo_infusao (nr_prescricao_p bigint, nr_sequencia_p bigint, ds_tipo_p text, ie_bomba_infusao_p text, ds_justificativa_p text) AS $body$
BEGIN

if (ds_tipo_p = 'SOL') then
	update	prescr_solucao a
	set    	a.ie_bomba_infusao     	= ie_bomba_infusao_p,
		a.ds_justif_troca_disp 	= ds_justificativa_p
	where  	a.nr_prescricao 	= nr_prescricao_p
	and    	a.nr_seq_solucao 	= nr_sequencia_p;
elsif (ds_tipo_p = 'SNE') then
	update 	prescr_material a
	set    	a.ie_bomba_infusao     	= ie_bomba_infusao_p,
		a.ds_justif_troca_disp 	= ds_justificativa_p
	where  	a.nr_prescricao 	= nr_prescricao_p
	and	a.ie_agrupador 		= 8
	and    	a.nr_sequencia		= nr_sequencia_p;
elsif (ds_tipo_p = 'MED') then
	update 	prescr_material a
	set    	a.ie_bomba_infusao     	= ie_bomba_infusao_p,
		a.ds_justif_troca_disp 	= ds_justificativa_p
	where  	a.nr_prescricao 	= nr_prescricao_p
	and	a.ie_agrupador 		= 1
	and    	a.nr_sequencia		= nr_sequencia_p;
elsif (ds_tipo_p = 'DIA') then 	--Dialise/Solucao
	update	prescr_solucao a
	set    	a.ie_bomba_infusao     	= ie_bomba_infusao_p,
		a.ds_justif_troca_disp 	= ds_justificativa_p
	where  	a.nr_prescricao 	= nr_prescricao_p
	and    	a.nr_seq_solucao 	= nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
