CREATE OR REPLACE PROCEDURE alterar_dt_credito_escrit ( nr_sequencia_p text, dt_credito_p timestamp) AS $body$
DECLARE

 
			 
nr_titulo_w		titulo_receber_liq.nr_titulo%type;
nr_seq_baixa_w	titulo_receber_liq.nr_sequencia%type;			
			 
c01 CURSOR FOR 
	/*select 	a.nr_titulo 
	from 	titulo_receber_cobr a 
	where	a.nr_seq_cobranca = nr_sequencia_p;*/
 
	/*OS 1405104 - Mudei para buscar todas as baixas dos títulos da cobrança para sofrer alteração.*/
 
	SELECT 		a.nr_titulo, 
				a.nr_sequencia 
	from 		titulo_receber_liq a 
	where		a.nr_seq_cobranca = nr_sequencia_p 
	order by 	a.nr_titulo, 
				a.nr_sequencia;


BEGIN 
 
update 	cobranca_escritural 
set 	dt_credito_bancario = dt_credito_p 
where 	nr_sequencia 		= nr_sequencia_p;
 
open c01;
loop 
fetch c01 into	 
	nr_titulo_w, 
	nr_seq_baixa_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin	 
	 
	/*select 	max(nr_sequencia) 
	into 	nr_seq_baixa_w 
	from 	titulo_receber_liq 
	where 	nr_titulo = nr_titulo_w 
	and 	nr_seq_cobranca = nr_sequencia_p;*/
 
	 
	update 	titulo_receber_liq 
	set		dt_credito_banco 	= dt_credito_p, 
			dt_real_recebimento = dt_credito_p 
	where 	nr_sequencia 		= nr_seq_baixa_w 
	and		nr_titulo 			= nr_titulo_w;	
	 
	update 	titulo_receber_cobr 
	set		dt_liquidacao 		= dt_credito_p 
	where 	nr_titulo		 	= nr_titulo_w	 
	and		nr_seq_cobranca 	= nr_sequencia_p;
	 
	end;				
end loop;
close C01;	
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
