CREATE OR REPLACE PROCEDURE alterar_dt_entrega_item_solic ( nr_solic_compra_p bigint, nr_item_solic_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_item_solic_compra_w		bigint;
dt_solic_item_w			timestamp;
dt_solic_item_ant_w		timestamp;

C01 CURSOR FOR
	SELECT	dt_solic_item,
		nr_item_solic_compra
	from	solic_compra_item
	where	nr_solic_compra = nr_solic_compra_p
	and	nr_item_solic_compra <> nr_item_solic_compra_p;


BEGIN

select	trunc(dt_solic_item,'dd')
into STRICT	dt_solic_item_w
from	solic_compra_item
where	nr_solic_compra = nr_solic_compra_p
and	nr_item_solic_compra = nr_item_solic_compra_p;

open C01;
loop
fetch C01 into
	dt_solic_item_ant_w,
	nr_item_solic_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	solic_compra_item
	set	dt_solic_item = dt_solic_item_w
	where	nr_solic_compra = nr_solic_compra_p
	and	nr_item_solic_compra = nr_item_solic_compra_w;

	if (dt_solic_item_w IS NOT NULL AND dt_solic_item_w::text <> '') then
		update	solic_compra_item_entrega
		set	dt_entrega_solicitada = dt_solic_item_w
		where	nr_solic_compra = nr_solic_compra_p
		and	nr_item_solic_compra = nr_item_solic_compra_w
		and	trunc(dt_entrega_solicitada,'dd') = dt_solic_item_ant_w;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
