SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_dt_entrega_prescricao ( nr_prescricao_p bigint, dt_entrega_p timestamp, nr_atendimento_p bigint, ie_param_prescr_p text) AS $body$
DECLARE


nr_prescricao_w		bigint;

-- ie_param_prescr_p = 'S', prescrição selecionada
-- ie_param_prescr_p = 'TP' todas as prescrições
C01 CURSOR FOR
	SELECT	nr_prescricao
	from	prescr_medica
	where	nr_atendimento = nr_atendimento_p;


BEGIN

if (coalesce(nr_prescricao_p,0) > 0) then

	if (ie_param_prescr_p = 'S') then

		update	prescr_medica
		set	dt_entrega = dt_entrega_p
		Where 	nr_prescricao = nr_prescricao_p;

	elsif (ie_param_prescr_p = 'TP') then

		open C01;
		loop
		fetch C01 into
			nr_prescricao_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin

			update	prescr_medica
			set	dt_entrega = dt_entrega_p
			Where 	nr_prescricao = nr_prescricao_w;
			end;
		end loop;
		close C01;
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE alterar_dt_entrega_prescricao ( nr_prescricao_p bigint, dt_entrega_p timestamp, nr_atendimento_p bigint, ie_param_prescr_p text) FROM PUBLIC;

