CREATE OR REPLACE PROCEDURE alterar_dt_entrega_prescr_proc ( nr_prescricao_p text, nr_seq_prescr_p text, dt_entrega_p timestamp, nm_usuario_p text, ie_opcao_p text) AS $body$
DECLARE


dt_result_w	timestamp;


BEGIN

/*ie_opcao_p
- P - atualiza data de resultado do item da prescrição se a data de entrega for maior  */
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (dt_entrega_p IS NOT NULL AND dt_entrega_p::text <> '') then

	update 	prescr_medica
	set		nm_usuario = nm_usuario_p,
			dt_entrega = dt_entrega_p
	where 	nr_prescricao = nr_prescricao_p;

	if (ie_opcao_p = 'P') and (nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '') then

		select	max(dt_resultado)
		into STRICT	dt_result_w
		from	prescr_procedimento
		where	nr_prescricao = nr_prescricao_p
		and		nr_sequencia = nr_seq_prescr_p;

		if ((dt_entrega_p > dt_result_w) or (coalesce(dt_result_w::text, '') = '')) then
			update	prescr_procedimento
			set		dt_resultado = dt_entrega_p
			where	nr_prescricao = nr_prescricao_p
			and		nr_sequencia = nr_seq_prescr_p;

		end if;

	end if;

commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
